import React, { useEffect, useRef, useState } from 'react';

export default function GoogleMap() {
  const mapRef = useRef(null);
  const inputRef = useRef(null);
  const [map, setMap] = useState(null);
  const [autocomplete, setAutocomplete] = useState(null);

  useEffect(() => {
    const loadGoogleMaps = () => {
      const center = { lat: -34.6037, lng: -58.3816 };

      const gMap = new window.google.maps.Map(mapRef.current, {
        center,
        zoom: 13,
      });
      setMap(gMap);

      const marker = new window.google.maps.Marker({
        map: gMap,
        position: center,
        draggable: true,
      });

      const ac = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'],
        componentRestrictions: { country: 'ar' },
      });
      ac.setFields(['geometry', 'formatted_address']);
      setAutocomplete(ac);

      ac.addListener('place_changed', () => {
        const place = ac.getPlace();
        if (!place.geometry) return;

        const location = place.geometry.location;
        gMap.setCenter(location);
        marker.setPosition(location);
      });

      marker.addListener('dragend', () => {
        const geocoder = new window.google.maps.Geocoder();
        const position = marker.getPosition();

        geocoder.geocode({ location: position }, (results, status) => {
          if (status === 'OK' && results[0]) {
            inputRef.current.value = results[0].formatted_address;
          }
        });
      });
    };

    if (!window.google) {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyDojbUyQ30EbzMwNRXKlFYxMQmqRtOw1Y0&libraries=places`;
      script.async = true;
      script.defer = true;
      script.onload = loadGoogleMaps;
      document.body.appendChild(script);
    } else {
      loadGoogleMaps();
    }
  }, []);

  return (
    <div style={{ height: '100%', width: '100%' }}>
      <input
        ref={inputRef}
        type="text"
        placeholder="Buscar una ubicaciÃ³n"
        className="input-autocomplete"
      />
      <div ref={mapRef} style={{ width: '100%', height: '90%' }} />
    </div>
  );
}
