import React, { useEffect, useRef, useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet-control-geocoder';

export default function LeafletSearchMap() {
  const [position, setPosition] = useState([-34.6037, -58.3816]);
  const [searchText, setSearchText] = useState('');
  const markerRef = useRef(null);
  const mapRef = useRef();

  useEffect(() => {
    if (!mapRef.current) return;
    const map = mapRef.current;

    const geocoder = L.Control.geocoder({
      defaultMarkGeocode: false
    })
      .on('markgeocode', function (e) {
        const { center, name } = e.geocode;
        setPosition([center.lat, center.lng]);
        setSearchText(name);
        map.setView([center.lat, center.lng], 13);
      })
      .addTo(map);

    return () => map.removeControl(geocoder);
  }, [mapRef.current]);

  function MapEventHandler() {
    useMapEvents({
      click(e) {
        const { latlng } = e;
        setPosition([latlng.lat, latlng.lng]);
        const geocoder = L.Control.Geocoder.nominatim();
        geocoder.reverse(latlng, mapRef.current.options.crs.scale(mapRef.current.getZoom()), results => {
          if (results.length > 0) {
            setSearchText(results[0].name);
          }
        });
      }
    });
    return null;
  }

  return (
    <div style={{ height: '100%', width: '100%' }}>
      <input
        type="text"
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        placeholder="Buscar ubicaciÃ³n"
        className="input-autocomplete"
      />
      <MapContainer
        center={position}
        zoom={13}
        style={{ height: '90%', width: '100%' }}
        whenCreated={(mapInstance) => (mapRef.current = mapInstance)}
      >
        <TileLayer
          attribution='&copy; OpenStreetMap contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={position} ref={markerRef} />
        <MapEventHandler />
      </MapContainer>
    </div>
  );
}
