import React, { useState, useEffect } from 'react';
import Mapa from './Mapa';
import './App.css';

function App() {
  const [mensaje, setMensaje] = useState('');
  const [view, setView] = useState('home'); // 'home' | 'map'

  useEffect(() => {
    // sigues trayendo el mensaje del backend si lo necesitas en home
    fetch('http://localhost:5000/api/hello')
      .then(res => res.json())
      .then(data => setMensaje(data.message))
      .catch(() => setMensaje('Error conectando al backend'));
  }, []);

  // Vista del mapa
  if (view === 'map') {
    return (
      <div className="App">
        <header className="header">
          <h1>Mi Mapa</h1>
        </header>
        <section className="card map-card">
          <Mapa />
        </section>
        <button className="btn" onClick={() => setView('home')}>
          ← Volver
        </button>
      </div>
    );
  }

  // Vista principal
  return (
    <div className="App">
      <header className="header">
        <h1>Página Principal</h1>
      </header>

      <section className="card">
        <h2>Mensaje del Backend</h2>
        <p className="message">{mensaje}</p>
      </section>

      <section className="card">
        <h2>Acciones</h2>
        <div className="btn-group">
          <button className="btn" onClick={() => setView('map')}>
            Ver Mapa
          </button>
          <button className="btn" onClick={() => { /* no hace nada */ }}>
            Botón Inactivo
          </button>
        </div>
      </section>
    </div>
  );
}

export default App;
